	// Function to let the User edit their profile on the frontend.
	function medwestEditFrontendProfile(){

		
		// When the 'Save' button is clicked at the top of the dashboard...
		$( '.medwest-frontend-edit-profile' ).on( 'click', function() {

			console.log('click')

			if( 'false' === $( this ).attr('data-saveedits') ){
				$( this ).text( 'Save Profile Edits' );
				$( this ).attr('data-saveedits', 'true');

				$( '#medwest-loggedin-indiv-wrapper-actual-data-email-edit' ).removeAttr( 'disabled' );
				$( '#medwest-loggedin-indiv-wrapper-actual-data-email-edit' ).focus();

			} else {

				$( this ).text( 'Edit Profile' );
				$( this ).attr('data-saveedits', 'false');
				$( '#medwest-loggedin-indiv-wrapper-actual-data-email-edit' ).attr( 'disabled', 'true' );


				var wpuserid = $( this ).attr( 'data-wpuserid' );
				var usertableid = $( this ).attr( 'data-usertableid' );
				var usernewemail = $( '#medwest-loggedin-indiv-wrapper-actual-data-email-edit' ).val();

				var data = {
		          'action': 'medwesthealthpoints_edit_user_profile_action',
		          'security': medwestHealthpointsPhpVariables.adminnonce19,
		          'wpuserid':wpuserid,
		          'usertableid':usertableid,
		          'usernewemail':usernewemail,
		        };

	        	console.log('This is the data being sent to the server to save a user\'s activity' );
	        	console.log(data);

				var request = $.ajax({
					url: ajaxurl,
					type: "POST",
					data:data,
					timeout: 0,
					success: function(response) {

						// Hide spinner
						//document.location.reload(true);
						console.log(response);

						
					},
					error: function(jqXHR, textStatus, errorThrown) {
						console.log(errorThrown);
						console.log(textStatus);
						console.log(jqXHR);
					}
				});

			}

			




			event.preventDefault ? event.preventDefault() : event.returnValue = false;

		});

	}

