	// Function for the user to record a new activity
	function medWestUserRecordActivity(){

		$( document ).on( 'change', '.medwest-activity-dropdown-actual', function( event ) {
			$( '#medwest-submit-activity-submissions-button' ).attr('data-activityselected', $(this).val() )
		});

		$( document ).on( 'click', '#medwest-submit-activity-submissions-button', function( event ) {

			$('#medwesthealthpoints-spinner-1').animate({'opacity':'1'})

			var activityname = $( this ).attr('data-activityselected')
			var activitycategory = $( this ).parent().find( '.medwest-activity-dropdown-actual' ).attr( 'data-category' );
			var activitydateperformed = $( '#medwest-date-performed-input' ).val();
			var activitysupportingdocs = $( '#medwest-form-vetdd214-0' ).val();
			var activitywpuserid = $( this ).attr( 'data-wpuserid' );
			var activityemployeeid = $( this ).attr( 'data-activityemployeeid' );
			var required = $( this ).parent().find( '.medwest-activity-dropdown-actual option:selected', this).attr('data-requireddocs');


			var proceed = true;
			if ( undefined === activityname ) {
				alert( 'Whoops! Looks like you forgot to select the Activity you completed!' );
				$('#medwesthealthpoints-spinner-1').animate({'opacity':'0'})
				proceed = false;
				return false;
			}

			if ( '' === activitydateperformed ) {
				alert( 'Whoops! Looks like you forgot to enter the Date This Activity Was Performed!' );
				$('#medwesthealthpoints-spinner-1').animate({'opacity':'0'})
				proceed = false;
				return false;
			}

			if ( '' === activitysupportingdocs && 'Yes' === required ) {
				alert( 'Whoops! Looks like you forgot to provide Supporting Documentation for this Activity!' );
				$('#medwesthealthpoints-spinner-1').animate({'opacity':'0'})
				proceed = false;
				return false;
			}



			if ( proceed ) {

				var data = {
		          'action': 'medwesthealthpoints_save_activity_user_action',
		          'security': medwestHealthpointsPhpVariables.adminnonce2,
		          'activityname':activityname,
		          'activitycategory':activitycategory,
		          'activitydateperformed':activitydateperformed,
		          'activitysupportingdocs':activitysupportingdocs,
		          'activitywpuserid': activitywpuserid,
		          'activityemployeeid': activityemployeeid,
		          'required':required,
		        };

	        	console.log('This is the data being sent to the server to save a user\'s activity' );
	        	console.log(data);

				var request = $.ajax({
					url: ajaxurl,
					type: "POST",
					data:data,
					timeout: 0,
					success: function(response) {

						// Hide spinner
						document.location.reload(true);
						console.log(response);

						
					},
					error: function(jqXHR, textStatus, errorThrown) {
						console.log(errorThrown);
						console.log(textStatus);
						console.log(jqXHR);
					}
				});
			}
		});
	}


